import React from 'react';
import './../css/main-page.css';

class TicTacToe extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape: "",
                },
                {
                    id: 12,
                    status: false,
                    shape: "",
                },
                {
                    id: 13,
                    status: false,
                    shape: "",
                },
                {
                    id: 21,
                    status: false,
                    shape: "",
                },
                {
                    id: 22,
                    status: false,
                    shape: "",
                },
                {
                    id: 23,
                    status: false,
                    shape: "",
                },
                {
                    id: 31,
                    status: false,
                    shape: "",
                },
                {
                    id: 32,
                    status: false,
                    shape: "",
                },
                {
                    id: 33,
                    status: false,
                    shape: "",
                },
            ]
        }

        this.play = this.play.bind(this);
    };


    componentDidMount() {

    }

    componentWillUnmount() {

    }


    setButton(buttonId) {
        let buttonsTemp = this.state.buttons;
        let buttonTemp = buttonsTemp.find(button => button.id === buttonId);
        let oPlayingTemp = false;



        if (!buttonTemp.status) {
            if (this.state.oPlaying) {
                buttonTemp.shape = "o";
                buttonTemp.status = true;
            } else {
                buttonTemp.shape = "x";
                buttonTemp.status = true;
                oPlayingTemp = true;
            }

            this.setState({
                buttons: buttonsTemp,
                oPlaying: oPlayingTemp
            })
        }
    }

    play(button) {
        this.setButton(button);
    }

    render() {
        return (
            <div className="principal-page">
                <div className="table-game">
                    <div className="first-row row">
                        {
                            this.state.buttons.filter((button) => button.id < 14).map((element) =>
                                <div key ={element.id}>
                                    <button id={element.id}className="button-size" onClick={() => this.play(element.id)}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                    <div className="second-row row">
                        {
                            this.state.buttons.filter((button) => (button.id > 14 && button.id < 24)).map((element) =>
                                <div key={element.id}>
                                    <button id={element.id} className="button-size" onClick={() => this.play(element.id)}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                    <div className="third-row row">
                        {
                            this.state.buttons.filter((button) => (button.id > 24 && button.id < 34)).map((element) =>
                                <div key={element.id}>
                                    <button id={element.id} className="button-size" onClick={() => this.play(element.id)}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                </div>
            </div>
        )
    }
}
export default TicTacToe;
