import React from 'react';
import './../css/main-page.css';
import swal from 'sweetalert';


class TicTacToe extends React.Component {

    constructor(props) {
        super(props);
        this.winningCombinations = [[11, 12, 13], [21, 22, 23], [31, 32, 33], [11, 21, 31],
        [12, 22, 32], [13, 23, 33], [11, 22, 33], [31, 22, 13]];
        this.state = {
            gameOver: false,
            oPlaying: false,
            keyboardPosition: 11,
            moving: "",
            usingKeyboard: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape: "",
                },
                {
                    id: 12,
                    status: false,
                    shape: "",
                },
                {
                    id: 13,
                    status: false,
                    shape: "",
                },
                {
                    id: 21,
                    status: false,
                    shape: "",
                },
                {
                    id: 22,
                    status: false,
                    shape: "",
                },
                {
                    id: 23,
                    status: false,
                    shape: "",
                },
                {
                    id: 31,
                    status: false,
                    shape: "",
                },
                {
                    id: 32,
                    status: false,
                    shape: "",
                },
                {
                    id: 33,
                    status: false,
                    shape: "",
                },
            ]
        }

        this.play = this.play.bind(this);
        this.restart = this.restart.bind(this);

    };


    componentDidMount() {
        console.log("DidMount");
        this.keyBoard();
    }

    componentWillUnmount() {
    }

    contains(buttonShape, combination) {
        return (buttonShape.includes(combination[0]) &&
            buttonShape.includes(combination[1]) &&
            buttonShape.includes(combination[2]));
    }

    winner(buttonShape) {
        let win = this.winningCombinations.filter(win => {
            return this.contains(buttonShape, win);
        })

        return (win.length > 0);
    }

    winShapeO(buttons, shape) {
        let buttonO = buttons.filter((button) => button.shape === shape).map(btn => { return btn.id });
        if (this.winner(buttonO)) {
            this.setState({
                gameOver: true,
            })
            this.displayPopUp(shape);
        }
    }

    generateRandom(buttons) {
        let availableSpaces = buttons.filter(button => button.status === false);
        const min = 0;
        const max = availableSpaces.length;
        const rand = min + Math.floor(Math.random() * (max - min));

        return availableSpaces[rand];
    }

    setButton(buttonId) {
        let buttonsTemp = this.state.buttons;
        let buttonTemp = buttonsTemp.find(button => button.id === buttonId);
        let oPlayingTemp = false;

        buttonTemp.shape = "x";
        buttonTemp.status = true;
        oPlayingTemp = true;
        this.setState({
            buttons: buttonsTemp,
            oPlaying: oPlayingTemp
        })
        let winX = this.winShapeO(this.state.buttons, "x");

        if (!winX) {
            buttonTemp = this.generateRandom(this.state.buttons);
            setTimeout(() => {
                buttonTemp.shape = "o";
                buttonTemp.status = true;
                oPlayingTemp = false;
                this.setState({
                    buttons: buttonsTemp,
                    oPlaying: oPlayingTemp
                })
                this.winShapeO(this.state.buttons, "o");
            }, 1000);
        }
    }

    displayPopUp(shape) {
        swal("Player " + shape + " win.");
    }

    play(button) {
        this.setButton(button);
    }

    setKeyPosition(position) {
        this.setState({
            keyboardPosition: position
        });
    }

    moveRight() {
        if (this.state.keyboardPosition === 11) {
            this.setState({
                keyboardPosition: 12
            });
        } else if (this.state.keyboardPosition === 12) {
            this.setState({
                keyboardPosition: 13
            })
        } else if (this.state.keyboardPosition === 21) {
            this.setState({
                keyboardPosition: 22
            })
        } else if (this.state.keyboardPosition === 22) {
            this.setState({
                keyboardPosition: 23
            })
        } else if (this.state.keyboardPosition === 31) {
            this.setState({
                keyboardPosition: 32
            })
        } else if (this.state.keyboardPosition === 32) {
            this.setState({
                keyboardPosition: 33
            })
        }
    }

    moveLeft() {
        if (this.state.keyboardPosition === 33) {
            this.setState({
                keyboardPosition: 32
            });
        } else if (this.state.keyboardPosition === 32) {
            this.setState({
                keyboardPosition: 31
            })
        } else if (this.state.keyboardPosition === 23) {
            this.setState({
                keyboardPosition: 22
            })
        } else if (this.state.keyboardPosition === 22) {
            this.setState({
                keyboardPosition: 21
            })
        } else if (this.state.keyboardPosition === 13) {
            this.setState({
                keyboardPosition: 12
            })
        } else if (this.state.keyboardPosition === 12) {
            this.setState({
                keyboardPosition: 11
            })
        }
    }

    moveDown() {
        if (this.state.keyboardPosition === 11) {
            this.setState({
                keyboardPosition: 21
            });
        } else if (this.state.keyboardPosition === 21) {
            this.setState({
                keyboardPosition: 31
            })
        } else if (this.state.keyboardPosition === 12) {
            this.setState({
                keyboardPosition: 22
            })
        } else if (this.state.keyboardPosition === 22) {
            this.setState({
                keyboardPosition: 32
            })
        } else if (this.state.keyboardPosition === 13) {
            this.setState({
                keyboardPosition: 23
            })
        } else if (this.state.keyboardPosition === 23) {
            this.setState({
                keyboardPosition: 33
            })
        }
    }

    moveUp() {
        if (this.state.keyboardPosition === 31) {
            this.setState({
                keyboardPosition: 21
            });
        } else if (this.state.keyboardPosition === 21) {
            this.setState({
                keyboardPosition: 11
            })
        } else if (this.state.keyboardPosition === 32) {
            this.setState({
                keyboardPosition: 22
            })
        } else if (this.state.keyboardPosition === 22) {
            this.setState({
                keyboardPosition: 12
            })
        } else if (this.state.keyboardPosition === 33) {
            this.setState({
                keyboardPosition: 23
            })
        } else if (this.state.keyboardPosition === 23) {
            this.setState({
                keyboardPosition: 13
            })
        }
    }

    move(direction) {
        console.log(direction);
        if (direction === 39) {
            this.moveRight()
        } else if (direction === 37) {
            this.moveLeft();
        } else if (direction === 40) {
            this.moveDown();
        } else if (direction === 38) {
            this.moveUp();
        }
    }


    keyBoard() {

        //window.removeEventListener('keyup', event); 

        // document.addEventListener('keydown', (e) => {
        document.addEventListener('keydown', function (event) {
            if (e.keyCode == 38 || e.keyCode === 37 || e.keyCode === 39 || e.keyCode === 40) {
            this.setState({
                moving: e.key
            })
        }
            console.log(`Key: ${event.key} with keycode ${event.keyCode} has been pressed`);
        });

        // if (e.keyCode == 38 || e.keyCode === 37 || e.keyCode === 39 || e.keyCode === 40) {
        //     this.setState({
        //         moving: e.key
        //     })
        // }

        // if (!this.state.usingKeyboard) {
        //     this.setState({
        //         usingKeyboard: true,
        //         keyboardPosition: 11
        //     })
        // } else {
        //     this.move(e.keyCode);
        //     console.log("Moving");
        // } 

        // });

    }


    restart() {
        this.setState({
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape: "",
                },
                {
                    id: 12,
                    status: false,
                    shape: "",
                },
                {
                    id: 13,
                    status: false,
                    shape: "",
                },
                {
                    id: 21,
                    status: false,
                    shape: "",
                },
                {
                    id: 22,
                    status: false,
                    shape: "",
                },
                {
                    id: 23,
                    status: false,
                    shape: "",
                },
                {
                    id: 31,
                    status: false,
                    shape: "",
                },
                {
                    id: 32,
                    status: false,
                    shape: "",
                },
                {
                    id: 33,
                    status: false,
                    shape: "",
                },
            ]
        })
    }

    render() {
        return (
            <div className="principal-page">
                <div className="table-game">
                    <div className="first-row row">
                        {
                            this.state.buttons.filter((button) => button.id < 14).map((element) =>
                                <div key={element.id}>
                                    <button id={element.id} className="button-size" onClick={() => this.play(element.id)} disabled={this.state.oPlaying || this.state.gameOver}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                    <div className="second-row row">
                        {
                            this.state.buttons.filter((button) => (button.id > 14 && button.id < 24)).map((element) =>
