import React from 'react';
import './../css/main-page.css';

class TicTacToe extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape:"",
                },
                {
                    id: 12,
                    status: false,
                    shape:"",
                },
                {
                    id: 13,
                    status: false,
                    shape:"",
                },
                {
                    id: 21,
                    status: false,
                    shape:"",
                },
                {
                    id: 22,
                    status: false,
                    shape:"",
                },
                {
                    id: 23,
                    status: false,
                    shape:"",
                },
                {
                    id: 31,
                    status: false,
                    shape:"",
                },
                {
                    id: 32,
                    status: false,
                    shape:"",
                },
                {
                    id: 33,
                    status: false,
                    shape:"",
                },
            ]
        }

        this.play = this.play.bind(this);
        this.button11 = React.createRef();
    };


    componentDidMount(){

    }

    componentWillUnmount(){

    }


    setButton(buttonId) {
        let buttonsTemp = this.state.buttons;
        let buttonTemp = buttonsTemp.find(button => button.id === buttonId);
        let oPlayingTemp = false;

        if (this.state.oPlaying) {
            buttonTemp.shape = "o";
            buttonTemp.status = true;
        } else {
            buttonTemp.shape = "x";
            buttonTemp.status = true;
            oPlayingTemp = true;
        }

        this.setState({
            buttons: buttonTemp,
            oPlaying: oPlayingTemp
        })

    }

    play(button){
        switch (button) {
            case "11": 
                this.setButton(11);
            break;
            case "12": 
                this.setButton(12);
            break;
            case "13": 
                this.setButton(13);
            break;
            case "21": 
                this.setButton(21);
            break;
            case "22": 
                this.setButton(22);
            break;
            case "23": 
                this.setButton(23);
            break;
            case "31": 
                this.setButton(31);
            break;
            case "32": 
                this.setButton(32);
            break;
            case "33": 
                this.setButton(33);
            break;
        }
    }

    render(){
        return(
            <div className="principal-page">
                <div className="table-game">
                    <div className="first-row">
                        <button id="button11" className="button-size button-11" onClick={() => this.play("11")}/>
                        <button id="button12" className="button-size button-12" onClick={() => this.play("12")}/>
                        <button id="button13" className="button-size button-13" onClick={() => this.play("13")}/>
                    </div>
                    <div className="second-row">
                        <button id="button21" className="button-size button-21" onClick={() => this.play("21")}/>
                        <button id="button22" className="button-size button-22" onClick={() => this.play("22")}/>
                        <button id="button23" className="button-size button-23" onClick={() => this.play("23")}/>
                    </div>
                    <div className="third-row">
                        <button id="button31" className="button-size button-31" onClick={() => this.play("31")}/>
                        <button id="button32" className="button-size button-32" onClick={() => this.play("32")}/>
                        <button id="button33" className="button-size button-33" onClick={() => this.play("33")}/>
                    </div>
                </div>
            </div>
        )
    }
}
export default TicTacToe;
