import React from 'react';
import './../css/main-page.css';

class TicTacToe extends React.Component {

    constructor(props) {
        super(props);
        this.winningCombinations = [[11,12,13],[21,22,23],[31,32,33],[11,21,31],
        [12,22,32],[13,23,33],[11,22,33],[31,22,13]];
        this.state = {
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape: "",
                },
                {
                    id: 12,
                    status: false,
                    shape: "",
                },
                {
                    id: 13,
                    status: false,
                    shape: "",
                },
                {
                    id: 21,
                    status: false,
                    shape: "",
                },
                {
                    id: 22,
                    status: false,
                    shape: "",
                },
                {
                    id: 23,
                    status: false,
                    shape: "",
                },
                {
                    id: 31,
                    status: false,
                    shape: "",
                },
                {
                    id: 32,
                    status: false,
                    shape: "",
                },
                {
                    id: 33,
                    status: false,
                    shape: "",
                },
            ]
        }

        this.play = this.play.bind(this);
        this.restart = this.restart.bind(this);
    };


    componentDidMount() {

    }

    componentWillUnmount() {

    }

    contains(buttonShape, combination){
        console.log("Combinations", combination);
        console.log("Includes", buttonShape.includes(combination[0]));
        return (buttonShape.includes(combination[0]) && 
        buttonShape.includes(combination[1]) && 
        buttonShape.includes(combination[2]));
    }

    winner(buttonShape){
       let win = this.winningCombinations.some(win => {
           this.contains(buttonShape,win);
       })

       console.log("Win",win);
    }

    winShapeO(buttons){
        let buttonO = buttons.filter((button) => button.shape === "o").map(btn => {return btn.id});
        this.winner(buttonO);
    }

    winShapeX(buttons){
        let buttonX = buttons.filter((button) => button.shape === "x");
        //console.log("Buttons X", buttonX.includes(11,12,13)); 

    }

    generateRandom(buttons){
        let availableSpaces = buttons.filter(button => button.status === false);
        const min = 0;
        const max = availableSpaces.length;
        const rand = min + Math.floor(Math.random() * (max - min));

        return availableSpaces[rand];
    }

    setButton(buttonId) {
        let buttonsTemp = this.state.buttons;
        let buttonTemp = buttonsTemp.find(button => button.id === buttonId);
        let oPlayingTemp = false;

        buttonTemp.shape = "x";
        buttonTemp.status = true;
        oPlayingTemp = true;
        this.setState({
            buttons: buttonsTemp,
            oPlaying: oPlayingTemp
        })


        buttonTemp = this.generateRandom(this.state.buttons);
        setTimeout(() => {
            buttonTemp.shape = "o";
            buttonTemp.status = true;
            oPlayingTemp = false;
            this.setState({
                buttons: buttonsTemp,
                oPlaying: oPlayingTemp
            })
            this.winShapeO(this.state.buttons);
        }, 1000);
    }

    play(button) {
        this.setButton(button);
    }

    restart() {
        this.setState({
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape: "",
                },
                {
                    id: 12,
                    status: false,
                    shape: "",
                },
                {
                    id: 13,
                    status: false,
                    shape: "",
                },
                {
                    id: 21,
                    status: false,
                    shape: "",
                },
                {
                    id: 22,
                    status: false,
                    shape: "",
                },
                {
                    id: 23,
                    status: false,
                    shape: "",
                },
                {
                    id: 31,
                    status: false,
                    shape: "",
                },
                {
                    id: 32,
                    status: false,
                    shape: "",
                },
                {
                    id: 33,
                    status: false,
                    shape: "",
                },
            ]
        })
    }

    render() {
        return (
            <div className="principal-page">
                <div className="table-game">
                    <div className="first-row row">
                        {
                            this.state.buttons.filter((button) => button.id < 14).map((element) =>
                                <div key ={element.id}>
                                    <button id={element.id}className="button-size" onClick={() => this.play(element.id)} disabled={this.state.oPlaying}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                    <div className="second-row row">
                        {
                            this.state.buttons.filter((button) => (button.id > 14 && button.id < 24)).map((element) =>
                                <div key={element.id}>
                                    <button id={element.id} className="button-size" onClick={() => this.play(element.id)} disabled={this.state.oPlaying}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                    <div className="third-row row">
                        {
                            this.state.buttons.filter((button) => (button.id > 24 && button.id < 34)).map((element) =>
                                <div key={element.id}>
                                    <button id={element.id} className="button-size" onClick={() => this.play(element.id)} disabled={this.state.oPlaying}>
                                        <span className="shape">{element.shape}</span>
                                    </button>
                                </div>
                            )
                        }
                    </div>
                </div>
                <br/>
                <div>
                    <button onClick={() => this.restart()} disabled={!this.state.gameOver}>
                        <span className="shape">Play again</span>
                    </button>
                </div>
            </div>
        )
    }
}
export default TicTacToe;
