import React from 'react';
import './../css/main-page.css';

class TicTacToe extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            gameOver: false,
            oPlaying: false,
            buttons: [
                {
                    id: 11,
                    status: false,
                    shape:"",
                },
                {
                    id: 12,
                    status: false,
                    shape:"",
                },
                {
                    id: 13,
                    status: false,
                    shape:"",
                },
                {
                    id: 21,
                    status: false,
                    shape:"",
                },
                {
                    id: 22,
                    status: false,
                    shape:"",
                },
                {
                    id: 23,
                    status: false,
                    shape:"",
                },
                {
                    id: 31,
                    status: false,
                    shape:"",
                },
                {
                    id: 32,
                    status: false,
                    shape:"",
                },
                {
                    id: 33,
                    status: false,
                    shape:"",
                },
            ]
        }

        this.play = this.play.bind(this);
        //this.restart = this.restart.bind(this);
    };


    componentDidMount(){

    }

    componentWillUnmount(){

    }


    setButton11(buttonId) {
        let buttonsTemp = this.state.buttons;
        let buttonTemp = buttonsTemp.find(button => button.id === buttonId);

        if (this.state.oPlaying) {
            buttonTemp.shape = "o";
            buttonTemp.status = true;
            this.setState({
                oPlaying: false
            })
        } else {
            buttonTemp.shape = "x";
            buttonTemp.status = true;
            this.setState({
                oPlaying: true
            })
        }

        this.setState({
            buttons: buttonTemp
        })

        console.log(this.state.buttons);
    }

    play(button){
        switch (button) {
            case "11": 
                this.setButton11(11);
            break;
        }
    }

    render(){
        return(
            <div className="principal-page">
                <div className="table-game">
                    <div className="first-row">
                        <button className="button-size button-11" onClick={() => this.play("11")}/>
                        <button className="button-size button-12"/>
                        <button className="button-size button-13"/>
                    </div>
                    <div className="second-row">
                        <button className="button-size button-21"/>
                        <button className="button-size button-22"/>
                        <button className="button-size button-23"/>
                    </div>
                    <div className="second-row">
                        <button className="button-size button-31"/>
                        <button className="button-size button-32"/>
                        <button className="button-size button-33"/>
                    </div>
                </div>
            </div>
        )
    }
}
export default TicTacToe;
